package base;

import static org.junit.Assert.assertNotNull;
import static org.junit.Assert.assertThat;

import org.hamcrest.Matchers;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.test.SpringApplicationConfiguration;
import org.springframework.boot.test.TestRestTemplate;
import org.springframework.boot.test.WebIntegrationTest;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.util.LinkedMultiValueMap;
import org.springframework.util.MultiValueMap;
import org.springframework.web.client.RestTemplate;

import com.tomtop.base.Application;

/**
 * 单元测试类
 * 
 * @author shuliangxing
 *
 * @date 2016年5月6日 下午3:38:44
 */
@RunWith(SpringJUnit4ClassRunner.class)
@SpringApplicationConfiguration(classes = Application.class)
// @WebAppConfiguration // 使用@WebIntegrationTest注解需要将@WebAppConfiguration注释掉
@WebIntegrationTest("server.port:0")
// 使用0表示端口号随机，也可以具体指定如8888这样的固定端口
public class UnitTest {

	private RestTemplate template = new TestRestTemplate();

	// 注入端口号
	@Value("${local.server.port}")
	private int port;

	/**
	 * 测试访问日志新增接口
	 */
	@Test
	public void testAddVistLog() {
		String url = "http://localhost:" + port + "/base/visitLog/v1/add";
		MultiValueMap<String, Object> map = new LinkedMultiValueMap<String, Object>();
		// 特殊字符和长度测试
		map.add("aid",
				"tes@t<2>2<>2<><>");
		map.add("website", 1);
		map.add("uip", "127.0.0.1");
		map.add("path", "www.baidu.com");
		map.add("source", "test.tomtop");
		String result = template.postForObject(url, map, String.class);
		System.out.println(result);
		assertNotNull(result);
		assertThat(result, Matchers.containsString("操作成功"));
	}

}
